declare type KV = {
    [key: string]: string;
};
/**
 * 给指定url，添加参数
 * @param url 目标url
 * @param paramsObj 要添加的参数对象
 * @return 操作后的url
 */
export declare const urlAddParams: (url: string, paramsObj: {
    [key: string]: string | number;
}) => string;
/**
 * 解析url的search部分为对象
 * @param params url的search部分，如a=10&b=abc
 * @returns  `{[key:string]:string}` 转换后的对象
 */
export declare const urlParseParams: (params: string) => KV;
/**
 * 将对象转换为url的search部分
 * number string boolean类型的值转换为字符串，其他的调用JSON.stringify尝试转换。并对结果进行encodeURIComponent
 * @param obj 要转换的对象
 * @returns 结果的字符串
 */
export declare const objToParams: (obj: {
    [key: string]: any;
}) => string;
export {};
